# https://adventofcode.com/2022/day/6
import sys

sample = "mjqjpqmgbljsphdztnvjfqwrcgsmlb"
input = ""
if __name__ == "__main__":
    data = input
    window = []
    ans = -1
    for i,char in enumerate(data):
        all_unique = True
        if len(window) < 14:
            window.append(char)
        else:
            window.pop(0)
            window.append(char)
        if len(window) == 14:
            s = set()
            for c in window:
                if c in s:
                    all_unique = False
                    break
                s.add(c)
            if all_unique:
                ans = i
                break
    print(f"First index where all chars are unique: {ans+1}")